import { LinearMediaPlayer } from "@/registry/default/blocks/linear-player/components/media-player"

export default function Page() {
  return (
    <section className="flex h-dvh w-dvw bg-black">
      <LinearMediaPlayer as="audio" />
    </section>
  )
}

"use client"

import {
  SpeakerHighIcon,
  SpeakerLowIcon,
  SpeakerXIcon,
} from "@phosphor-icons/react"

import { Button } from "@/registry/default/blocks/linear-player/ui/button"
import { useMediaStore } from "@/registry/default/ui/media-provider"
import { MuteControl } from "@/registry/default/ui/mute-control"

export function VolumeStateControl() {
  const muted = useMediaStore((state) => state.muted)
  const volume = useMediaStore((state) => state.volume)

  return (
    <MuteControl asChild>
      <Button className="cursor-pointer" size="icon" variant="glass">
        {muted || volume === 0 ? (
          <SpeakerXIcon weight="fill" />
        ) : volume < 0.5 ? (
          <SpeakerLowIcon weight="fill" />
        ) : (
          <SpeakerHighIcon weight="fill" />
        )}
      </Button>
    </MuteControl>
  )
}

"use client"

import {
  CircleNotchIcon,
  PauseIcon,
  PlayIcon,
  RepeatIcon,
} from "@phosphor-icons/react"

import { Button } from "@/registry/default/blocks/linear-player/ui/button"
import { useMediaStore } from "@/registry/default/ui/media-provider"
import { PlaybackControl } from "@/registry/default/ui/playback-control"

export function PlaybackStateControl() {
  const status = useMediaStore((state) => state.status)

  return (
    <PlaybackControl asChild>
      <Button className="cursor-pointer" size="icon" variant="glass">
        {status === "playing" ? (
          <PauseIcon weight="fill" />
        ) : status === "ended" ? (
          <RepeatIcon />
        ) : status === "buffering" || status === "loading" ? (
          <CircleNotchIcon className="animate-spin" weight="bold" />
        ) : (
          <PlayIcon weight="fill" />
        )}
      </Button>
    </PlaybackControl>
  )
}

"use client"

import { useState } from "react"

import { Button } from "@/registry/default/blocks/linear-player/ui/button"
import { useMediaStore } from "@/registry/default/ui/media-provider"
import * as TimelineSlider from "@/registry/default/ui/timeline-control"
import {
  Duration,
  Elapsed,
  HoverTime,
  LiveLatency,
  Remaining,
} from "@/registry/default/ui/timeline-labels"

export function TimelineSliderControl() {
  const [showRemaining, setShowRemaining] = useState(false)
  const liveLatency = useMediaStore((state) => state.liveLatency)
  const isLive = useMediaStore((state) => state.isLive)
  const player = useMediaStore((state) => state.player)

  return (
    <div className="my-auto flex grow items-center gap-3 select-none">
      {!isLive && <Elapsed className="text-xs font-medium" />}
      <div className="group/timeline relative w-full grow">
        <TimelineSlider.Root className="group focus-area cursor-crosshair -focus-area-x-2 -focus-area-y-14">
          <TimelineSlider.Track className="overflow-hidden">
            <TimelineSlider.Progress />
            <TimelineSlider.Buffered variant="combined" />
          </TimelineSlider.Track>
          <TimelineSlider.Thumb
            className={`
              absolute h-8 w-px rounded-full bg-primary/60 opacity-0 transition-opacity duration-300
              group-hover/timeline:opacity-100
              group-active/timeline:bg-white
            `}
            showWithHover
          />
          <TimelineSlider.Thumb
            className={`
              top-auto! bottom-[calc(100%+16px)] flex h-auto w-fit bg-transparent text-xs font-medium opacity-0 transition-opacity duration-300
              group-hover/timeline:opacity-100
            `}
            showWithHover
          >
            <HoverTime />
            {!isLive && (
              <>
                &nbsp;/&nbsp;
                <Duration className="text-primary/60" />
              </>
            )}
          </TimelineSlider.Thumb>
        </TimelineSlider.Root>
      </div>
      {!isLive && (
        <Button
          aria-label={
            showRemaining ? "Show total duration" : "Show remaining time"
          }
          aria-pressed={showRemaining}
          className={`
            h-6 w-14 cursor-pointer
            hover:bg-transparent
            focus-visible:bg-transparent
          `}
          onClick={() => {
            setShowRemaining(!showRemaining)
          }}
          size="sm"
          variant="glass"
        >
          {showRemaining ? (
            <Remaining className="text-xs font-medium" />
          ) : (
            <Duration className="text-xs font-medium" />
          )}
        </Button>
      )}
      {liveLatency && player && liveLatency > 1 && (
        <>
          <LiveLatency className="text-xs font-medium" />
          <Button
            aria-label="Go to live"
            className="h-6 w-fit cursor-pointer"
            onClick={() => void player.goToLive()}
            size="icon"
            variant="glass"
          >
            <span className="text-xs font-medium text-primary">Go to live</span>
          </Button>
        </>
      )}
      {liveLatency && liveLatency <= 1 && (
        <div className="flex items-center rounded-full bg-red-600 px-2 py-0.5 text-xs font-semibold tracking-wide">
          <div className="mr-1 h-2 w-2 animate-caret-blink rounded-full bg-foreground" />
          <span className="tracking-widest">LIVE</span>
        </div>
      )}
    </div>
  )
}

import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"
import * as React from "react"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  `
    inline-flex shrink-0 cursor-pointer items-center justify-center gap-2 rounded-md text-sm font-medium whitespace-nowrap transition-colors
    focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-primary/50
    disabled:pointer-events-none disabled:opacity-50
    [&_svg]:pointer-events-none [&_svg]:shrink-0
    [&_svg:not([class*='size-'])]:size-4
  `,
  {
    defaultVariants: {
      size: "default",
      variant: "default",
    },
    variants: {
      size: {
        default: `
          h-9 px-4 py-2
          has-[>svg]:px-3
        `,
        icon: "size-8 rounded-md p-2",
        lg: `
          h-10 rounded-md px-6
          has-[>svg]:px-4
        `,
        sm: `
          h-8 gap-1.5 rounded-md px-3
          has-[>svg]:px-2.5
        `,
      },
      variant: {
        default: `
          bg-primary text-primary-foreground
          hover:bg-primary/90
        `,
        ghost: `hover:bg-accent hover:text-accent-foreground`,
        glass: `
          bg-transparent text-primary
          hover:bg-primary/10
          active:scale-[0.97]
        `,
        link: `
          text-primary underline-offset-4
          hover:underline
        `,
        outline: `
          border border-input bg-background
          hover:bg-accent hover:text-accent-foreground
        `,
        secondary: `
          bg-secondary text-secondary-foreground
          hover:bg-secondary/80
        `,
      },
    },
  }
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ asChild = false, className, size, variant, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ className, size, variant }))}
        data-slot="button"
        ref={ref}
        {...props}
      />
    )
  }
)

Button.displayName = "Button"

export { Button, buttonVariants }

"use client"

import type shaka from "shaka-player"

import { CardsThreeIcon, PlayIcon } from "@phosphor-icons/react"
import { useEffect } from "react"

import type { Asset } from "@/registry/default/hooks/use-asset"

import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"
import { Button } from "@/registry/default/blocks/linear-player/ui/button"
import { useAsset } from "@/registry/default/hooks/use-asset"
import { useMediaStore } from "@/registry/default/ui/media-provider"

/**
 * Demo assets for the linear-player
 */
export const ASSETS: Asset[] = [
  {
    config: {
      drm: {
        advanced: {
          "com.widevine.alpha": {
            serverCertificateUri:
              "https://cwip-shaka-proxy.appspot.com/service-cert",
          },
        },
        servers: {
          "com.widevine.alpha": "https://cwip-shaka-proxy.appspot.com/no_auth",
        },
      } as unknown as shaka.extern.DrmConfiguration,
    } as shaka.extern.PlayerConfiguration,
    description:
      "A Blender Foundation short film, protected by Widevine encryption",
    id: "sintel",
    poster: "https://storage.googleapis.com/shaka-asset-icons/sintel.png",
    src: "https://storage.googleapis.com/shaka-demo-assets/sintel-widevine/dash.mpd",
    title: "Blender Foundation - Sintel",
  },
  {
    description: "Media Tailor HLS Stream",
    id: "sing2",
    poster: "https://storage.googleapis.com/shaka-asset-icons/sing.png",
    src: "https://ad391cc0d55b44c6a86d232548adc225.mediatailor.us-east-1.amazonaws.com/v1/master/d02fedbbc5a68596164208dd24e9b48aa60dadc7/singssai/master.m3u8",
    title: "Sing 2 Trailer",
  },
  {
    description: "A Blender Foundation short film, Media Tailor Live DASH",
    id: "bbb",
    poster:
      "https://storage.googleapis.com/shaka-asset-icons/big_buck_bunny.png",
    src: "https://d305rncpy6ne2q.cloudfront.net/v1/dash/94063eadf7d8c56e9e2edd84fdf897826a70d0df/SFP-MediaTailor-Live-HLS-DASH/channel/sfp-channel1/dash.mpd",
    title: "Big Buck Bunny",
  },
  {
    description: "HLS Video",
    id: "natgeo",
    poster: "https://demo.theoplayer.com/hubfs/videos/natgeo/poster.jpg",
    src: "https://demo.theoplayer.com/hubfs/videos/natgeo/playlist.m3u8",
    title: "National Geographic - VR equirectangular",
  },
]

export function Playlist() {
  const currentItem = useMediaStore((state) => state.currentItem)
  const player = useMediaStore((state) => state.player)

  const { getCurrentItem, isPreloaded, loadPlaylist, preloadAsset, skipToId } =
    useAsset()

  useEffect(() => {
    if (!player) return

    loadPlaylist(ASSETS)
  }, [player])

  const handleAssetSelect = async (asset: Asset) => {
    await skipToId(asset.id)
  }

  const handleAssetHover = async (asset: Asset) => {
    if (!isPreloaded(asset.id) && getCurrentItem()?.id !== asset.id) {
      await preloadAsset(asset)
    }
  }

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button aria-label="Open Playlist" size="icon" variant="glass">
          <CardsThreeIcon weight="fill" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent
        align="end"
        alignOffset={-12}
        className="dark w-sm border border-border p-2"
        side="top"
        sideOffset={24}
      >
        <DropdownMenuLabel>Playlist</DropdownMenuLabel>
        <DropdownMenuSeparator />
        <div className="space-y-2">
          {ASSETS.map((asset) => {
            const isCurrentAsset = currentItem?.id === asset.id
            const isAssetPreloaded = isPreloaded(asset.id)

            return (
              <DropdownMenuItem
                className={`
                  dark p-0 transition-colors
                  ${
                    isCurrentAsset
                      ? "border-primary/20 bg-primary/10"
                      : "hover:bg-accent/50"
                  }
                `}
                key={asset.id}
                onMouseEnter={() => handleAssetHover(asset)}
                onSelect={() => handleAssetSelect(asset)}
              >
                <div className="flex w-full items-center gap-3 p-2">
                  <div className="relative aspect-video w-20 shrink-0 overflow-hidden rounded">
                    <img
                      alt={asset.title}
                      className="object-cover"
                      sizes="80px"
                      src={asset.poster}
                    />
                    {isCurrentAsset && (
                      <div className="absolute inset-0 flex items-center justify-center bg-black/40">
                        <PlayIcon className="size-6 text-white" weight="fill" />
                      </div>
                    )}
                    {isAssetPreloaded && !isCurrentAsset && (
                      <div className="absolute top-1 right-1 size-2 rounded-full bg-green-500" />
                    )}
                  </div>
                  <div className="min-w-0 flex-1">
                    <div className="flex items-center gap-2">
                      <div className="truncate text-sm font-medium">
                        {asset.title}
                      </div>
                      {isCurrentAsset && (
                        <div className="flex size-2 rounded-full bg-primary" />
                      )}
                    </div>
                    {asset.description && (
                      <div className="line-clamp-2 text-xs text-muted-foreground">
                        {asset.description}
                      </div>
                    )}
                  </div>
                </div>
              </DropdownMenuItem>
            )
          })}
        </div>
      </DropdownMenuContent>
    </DropdownMenu>
  )
}

"use client"

import { ClosedCaptioningIcon } from "@phosphor-icons/react"

